// The received message is stored in 'msg'
// It will have at least a 'payload' property:
//   console.log(msg.payload);
// The 'context' object is available to store state
// between invocations of the function
//   context = {};

// this assign the cheerio selection to $
var $ = msg.$;

// msg.selection iteration 
msg.payload = (function (selection) {
	var urls = [];
	selection.each(function(i, elem) {
  		urls[i] = $(this).attr("href");
	});
	return urls;
})(msg.selection);

// modify content-type header
msg.headers["content-type"] = "application/json";

// delete not useful nodes
msg.selection = msg.$ = undefined;

return msg;
